mapper.select:
SELECT CONCAT('p',pcode) AS id, 'p0' as pId , pname as name FROM tb_telno_province

  WHERE pcode IN ( SELECT DISTINCT
  pcode
  FROM tb_telno_city

  WHERE ccode NOT IN ( SELECT city_code FROM tb_cc_area_city ac
  WHERE ac.sid = #{sid,jdbcType=VARCHAR}

    AND area_id != #{areaId,jdbcType=VARCHAR}
  )
  AND (ccode < 1000 OR sid = #{sid,jdbcType=VARCHAR})
  )


UNION ALL

SELECT CONCAT('c',ccode) AS id, CONCAT('p',pcode) AS pId, cname as name FROM tb_telno_city

  WHERE ccode NOT IN (
  SELECT city_code FROM tb_cc_area_city ac
  WHERE ac.sid = #{sid,jdbcType=VARCHAR}

    AND area_id != #{areaId,jdbcType=VARCHAR}

  )
  AND (ccode < 1000 OR sid = #{sid,jdbcType=VARCHAR})

SELECT DISTINCT CONCAT('c',ac.city_code) as id, CONCAT('p',tc.pcode) AS pId, cname as name FROM
tb_cc_area_city ac LEFT JOIN tb_telno_city tc ON ac.city_code = tc.ccode
WHERE
ac.sid = #{sid,jdbcType=VARCHAR}
AND ac.city_code IN
#{code}

  AND ac.area_id != #{areaId,jdbcType=VARCHAR}

SELECT CONCAT('c',tc.ccode) AS id, CONCAT('p', tc.pcode) AS pId, tc.cname as name FROM tb_cc_area_city ac INNER JOIN tb_telno_city tc ON ac.city_code = tc.ccode WHERE ac.area_id = #{areaId,jdbcType=VARCHAR}
UNION ALL
SELECT CONCAT('p',tp.pcode) AS id, 'p0' as pId , tp.pname as name FROM tb_telno_province tp WHERE tp.pcode in ( SELECT tc.pcode FROM tb_cc_area_city ac INNER JOIN tb_telno_city tc ON ac.city_code = tc.ccode WHERE ac.area_id = #{areaId,jdbcType=VARCHAR} )
