mapper.select:

select c.* ,d.app_name from  (select a.city , a.area_code ,a.id ,  b.appid ,b.addtime, b.number ,b.`status` from  tb_city_area_code  a, tb_mask_num b  where   a.id = b.cityid ) c, tb_app_info d
where d.appid = c.appid and c.`status` in  ('01','02')
and d.sid = #{params.sid,jdbcType=VARCHAR}

  and c.number like CONCAT('%',#{params.number,jdbcType=VARCHAR},'%')


  and d.appid = #{params.appid,jdbcType=VARCHAR}


  and c.id = #{params.id,jdbcType=VARCHAR}

ORDER BY c.addtime desc

select c.* ,d.app_name from  (select a.city , a.area_code ,a.id ,  b.appid ,b.addtime, b.number ,b.`status` from  tb_city_area_code  a, tb_mask_num b  where   a.id = b.cityid ) c, tb_app_info d
where d.appid = c.appid and c.`status` in  ('01','02')
and d.sid = #{params.sid,jdbcType=VARCHAR}

  and c.number like CONCAT('%',#{params.number,jdbcType=VARCHAR},'%')


  and d.appid = #{params.appid,jdbcType=VARCHAR}


  and c.id = #{params.id,jdbcType=VARCHAR}

ORDER BY c.addtime desc
