mapper.select:
SELECT tmp.*, app.app_name as appName FROM (SELECT a.statday statDay,a.appid,sum(a.fee) AS fee,sum(a.recordingfee) AS recordingFee FROM stat_sp_day_record a WHERE 1=1
and feeid = #{params.feeid,jdbcType=VARCHAR}
and a.appid = #{params.appId,jdbcType=VARCHAR}
AND DATE_FORMAT(a.statday,'%Y-%m-%d') >=DATE_FORMAT(#{startTime,jdbcType=TIMESTAMP},'%Y-%m-%d')
AND DATE_FORMAT(a.statday,'%Y-%m-%d') <= DATE_FORMAT( #{endTime,jdbcType=TIMESTAMP},'%Y-%m-%d')
GROUP BY a.appid, a.statday ) AS tmp LEFT JOIN tb_app_info app ON tmp.appid = app.appid order by statday DESC

SELECT tmp.*, app.app_name as appName FROM (SELECT date_format(a.smonth, '%Y-%m') AS smonth,a.appid,sum(a.fee) AS fee,sum(a.recordingfee) AS recordingFee,sum(rent) AS rent,sum(min_consume) AS costMin,count(DISTINCT(sipphone)) AS sipNum FROM stat_sp_consume_record a WHERE 1=1
and feeid = #{params.feeid,jdbcType=VARCHAR}
and a.appid = #{params.appId,jdbcType=VARCHAR}
AND DATE_FORMAT(a.smonth,'%Y-%m') = DATE_FORMAT( #{ym,jdbcType=TIMESTAMP},'%Y-%m')
GROUP BY a.appid, date_format(a.smonth, '%y-%M')) AS tmp LEFT JOIN tb_app_info app ON tmp.appid = app.appid order by tmp.smonth DESC

SELECT tmp.*, app.app_name as appName FROM (SELECT date_format(a.smonth, '%Y-%m') AS smonth,a.appid,sum(a.fee) AS fee,sum(a.recordingfee) AS recordingFee,sum(rent) AS rent,sum(min_consume) AS costMin,count(DISTINCT(sipphone)) AS sipNum FROM stat_sp_consume_record a WHERE 1=1
and feeid = #{params.feeid,jdbcType=VARCHAR}
and a.appid = #{params.appId,jdbcType=VARCHAR}
AND DATE_FORMAT(a.smonth,'%Y-%m') = DATE_FORMAT( #{ym,jdbcType=TIMESTAMP},'%Y-%m')
GROUP BY a.appid, date_format(a.smonth, '%y-%M')) AS tmp LEFT JOIN tb_app_info app ON tmp.appid = app.appid order by tmp.smonth DESC

SELECT tmp.*, app.app_name as appName
FROM
(SELECT date_format(a.smonth, '%Y-%m') AS smonth,a.appid,a.fee AS fee,a.recordingfee AS recordingFee,rent ,min_consume AS minConsume,sipphone, used_days as usedDays,ifnull(status,0)as status
FROM stat_sp_consume_record a
WHERE  1=1


  and feeid  = #{params.feeid,jdbcType=VARCHAR}



  and a.appid  = #{params.appId,jdbcType=VARCHAR}


  AND DATE_FORMAT(a.smonth,'%Y-%m') = #{params.smonth,jdbcType=TIMESTAMP}

) AS tmp

LEFT JOIN tb_app_info app ON tmp.appid = app.appid
order by tmp.smonth DESC
