mapper.select:
select a.*,b.name,c.uid,c.sid from rms_axb_rate as a, tb_user_admin_authen_company as b, tb_user_admin as c where a.feeid = c.feeid and c.uid = b.uid

  and c.uid = #{uid,jdbcType=VARCHAR}


  and c.sid = #{sid,jdbcType=VARCHAR}


  and c.feeid = #{feeid,jdbcType=VARCHAR}

select a.*,b.name,c.uid,c.sid from rms_axb_rate as a, tb_user_admin_authen_company as b, tb_user_admin as c where a.feeid = c.feeid and c.uid = b.uid
and c.uid = #{params.uid,jdbcType=VARCHAR}
and c.sid = #{params.sid,jdbcType=VARCHAR}
and c.feeid = #{params.feeid,jdbcType=VARCHAR}
and b.name LIKE "%"#{params.name,jdbcType=VARCHAR}"%"
and a.end_date >= str_to_date(#{params.endDate,jdbcType=DATE},'%Y-%m-%d')
and a.end_date <= str_to_date(#{params.endDate1,jdbcType=DATE},'%Y-%m-%d')
and a.addtime >= str_to_date(#{params.addtime,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')
and a.addtime <= str_to_date(#{params.addtime1,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')
order by a.addtime DESC
